mapper.select:
select * from t_sys_monitor_log
where 1 = 1


    and operation_description like #{pattern}



    and operation_username like #{pattern}



    and client_ip like #{pattern}



    and operating_system_name like #{pattern}



    and browser like #{pattern}



    and request_uri like #{pattern}



    and request_method like #{pattern}


    and is_error = #{isError}


    and date_format(operation_time, '%Y-%m-%d %H:%i')  >=  date_format(#{sd}, '%Y-%m-%d %H:%i')
    and date_format(operation_time, '%Y-%m-%d %H:%i')  <=  date_format(#{ed}, '%Y-%m-%d %H:%i')

order by operation_time desc
